# Cell
import numpy as np
from sklearn.metrics import mean_squared_error, r2_score

# Cell

def rmse(obs, preds):
    return np.sqrt(mean_squared_error(obs, preds))

# Cell

def r2(obs, preds):
    return r2_score(obs, preds)